import React from "react"
import "./Sign-in.css"


const emailRegex = RegExp(/^[a-zA-Z0-9.!3$%&*+/=?^{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/)

const formValid = formError => {
    let valid = true

    Object.values.formError.forEach(val => val.length > 0 && (valid = false))
    return valid
}

class SignIn extends React.Component {
    constructor() {
        super()
        this.state = {
            email: "",
            password: "",
            formError: {
                email: "",
                password: ""
            }
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(e) {
        const { name, value} = e.target

        let formErrors = this.state.formError

        switch(name) {
            case "email":
                formErrors.email = emailRegex.test(value) && value.length > 0 ? "" : "Invalid Email address";
                break
            case "password":
                formErrors.password = value.length < 5 && value.length > 0 ? "Minimum of 5 character is expected" : "";
                break
            default:
                break
        }

    }
    handleSubmit(e) {
        e.preventDefault()
    }

    render() {
         return (
            <div className="form-field">
                <h1 className="form-header">Sign In as a Recycler</h1>
                <form className="form" onSubmit={this.handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input type="email" className="form-control" id="email" name="email" onChange={this.handleChange} />
                    </div>
                    <br />
                    <div>
                        <label>Password:</label>
                        <input type="password" className="form-control" id="password" name="password" onChange={this.handleChange} />
                    </div>
                    <br />
                    <div id="button">
                          <button  className="button" type="submit">Submit</button>
                    </div>
                  
                </form>
            </div>
        )
    }
       
}

export default SignIn
